INSERT INTO `tin_tuc` (`id_tin`, `tieude`, `noidung_tt`, `noidung`, `hinh_anh_tin`, `ngaydang`, `tacgia`, `created_at`, `updated_at`) VALUES
(6, 'Tổng hợp các ý tưởng trang trí Tết Tân Sửu độc đáo hút tài lộc vào nhà', 'Tết Tân Sửu đang đến gần, người người nhà nhà lại chuẩn bị dọn dẹp trang hoàng lại nhà cửa để đón Tết. Bạn đã có ý tưởng gì để trang trí cho tổ ấm của mình chưa? Hãy để Homedy gợi những cho bạn những ý tưởng trang trí Tết đẹp, độc đáo, đơn giản có ý nghĩa phong thủy cao hút tài, hút lộc vào nhà nhé!', '<p>Trong quan niệm của người Việt Nam, để một năm mới đến mang theo nhiều điều may mắn th&igrave; việc gột rửa sạch sẽ v&agrave; trang ho&agrave;ng nh&agrave; cửa để đ&oacute;n tết l&agrave; điều v&ocirc; c&ugrave;ng quan trọng. C&ugrave;ng kh&aacute;m ph&aacute; &yacute; nghĩa của &ldquo;c&ocirc;ng việc&rdquo; n&agrave;y v&agrave; những &yacute; tưởng trang ho&agrave;ng nh&agrave; của hot nhất 2021 nh&eacute;!</p>\r\n\r\n<h2>1. &Yacute; nghĩa truyền thống của những vật trang tr&iacute; Tết</h2>\r\n\r\n<p>B&aacute;nh chưng xanh, c&agrave;nh hoa tươi v&agrave; c&acirc;u đối đỏ l&agrave; những vật đặc trưng quen thuộc kh&ocirc;ng thể thiếu trong nh&agrave; của mỗi người d&acirc;n miền Bắc trong dịp Tết đến xu&acirc;n về. Kh&ocirc;ng chỉ mang kh&ocirc;ng kh&iacute; Tết tr&agrave;n ngập v&agrave;o ng&ocirc;i nh&agrave; của bạn m&agrave; những vật dụng n&agrave;y c&ograve;n được người xưa tin rằng n&oacute; c&oacute; thể xua đuổi ma quỷ, cầu cho năm mới b&igrave;nh an, ấm no.</p>\r\n\r\n<h3>Hoa mai&nbsp;</h3>\r\n\r\n<p><img alt=\"trang-tri-tet\" src=\"https://img.homedy.com/store/images/2020/12/25/trang-tri-tet-637445134310402048.jpg\" style=\"height:555px; width:800px\" /></p>\r\n\r\n<p><em>H&igrave;nh ảnh trang tr&iacute; Tết truyền thống quen thuộc</em></p>\r\n\r\n<p><strong>&gt;&gt;&gt; Xem th&ecirc;m:&nbsp;<a href=\"https://homedy.com/post/5fe5b331a9ee1237347a3521\">Trang tr&iacute; nh&agrave; tết cổ điển</a></strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong văn h&oacute;a xưa, hoa mai ch&iacute;nh l&agrave; biểu tượng tượng trưng cho sự gi&agrave;u sang, ph&uacute; qu&yacute;. Việc treo những chữ &ldquo;Lộc&rdquo; đỏ tr&ecirc;n c&agrave;nh mai, đ&agrave;o sẽ mang đến sự may mắn v&agrave; thu h&uacute;t t&agrave;i lộc tới ng&ocirc;i nh&agrave;.</p>\r\n\r\n<h3>Hoa đ&agrave;o</h3>\r\n\r\n<p><img alt=\"hoa-dao-trang-tri-tet\" src=\"https://img.homedy.com/store/images/2020/12/25/trang-tri-tet-dep-637445135295089676.jpg\" style=\"height:800px; width:800px\" /></p>\r\n\r\n<p><em>&nbsp;C&acirc;y đ&agrave;o bạch mang m&agrave;u sắc ph&ugrave; hợp với căn nh&agrave;</em></p>\r\n\r\n<p><strong>&nbsp;&gt;&gt;&gt; Xem th&ecirc;m:&nbsp;<a href=\"https://homedy.com/post/5fe5afeaa9ee1237347a34f2\">Trang tr&iacute; nh&agrave; với hoa đ&agrave;o bạch</a></strong></p>\r\n\r\n<p>Hoa đ&agrave;o được coi l&agrave; tinh hoa trong khối ngũ h&agrave;nh. Người xưa tin rằng đ&acirc;y l&agrave; loại hoa c&oacute; sức mạnh đổi mới, đem lại cho gia chủ sự sinh s&ocirc;i v&agrave; ph&aacute;t triển mạnh mẽ cho năm mới. Ng&agrave;y nay, th&uacute; chơi hoa đ&agrave;o ng&agrave;y c&agrave;ng ph&aacute;t triển, ngo&agrave;i những c&agrave;nh đ&agrave;o đỏ mang m&agrave;u sắc rực rỡ trong ng&agrave;y Tết th&igrave; đ&agrave;o phai, đ&agrave;o bạch, đ&agrave;o đ&ocirc;ng đỏ, &hellip; ng&agrave;y được rất nhiều gia đ&igrave;nh chọn b&agrave;y trong dịp Tết Nguy&ecirc;n Đ&aacute;n.</p>\r\n\r\n<p><img alt=\"trang-tri-ngay-tet\" src=\"https://img.homedy.com/store/images/2020/12/25/trang-tri-khong-khach-tet-637445137572037304.jpg\" style=\"height:533px; width:800px\" /></p>\r\n\r\n<p><em>Ph&ograve;ng kh&aacute;ch tr&agrave;n ngập kh&ocirc;ng kh&iacute; Tết của một gia chủ y&ecirc;u gốm sứ</em></p>\r\n\r\n<p><strong>&gt;&gt;&gt; Xem th&ecirc;m:&nbsp;<a href=\"https://homedy.com/post/5fe455fcca7d5e41f454cd29\">Tết truyền thống của một gia đ&igrave;nh y&ecirc;u gốm sứ</a></strong></p>\r\n\r\n<h3>Đ&egrave;n lồng đỏ</h3>\r\n\r\n<p>Đ&egrave;n lồng đỏ cũng l&agrave; biểu tượng may mắn m&agrave; ch&uacute;ng ta hay bắt gặp trong những ng&agrave;y lễ Tết. Việc treo đ&egrave;n lồng đỏ trước cửa c&oacute; &yacute; nghĩa xua đuổi t&agrave; ma, thu h&uacute;t may mắn. D&acirc;n gian cho rằng những chiếc đ&egrave;n đỏ treo trước nh&agrave; l&agrave; sự hiện th&acirc;n của b&igrave;nh y&ecirc;n v&agrave; hạnh ph&uacute;c.</p>\r\n\r\n<p><img alt=\"den-long-do\" src=\"https://img.homedy.com/store/images/2020/12/25/y-nghia-den-long-do-ngay-tet-637445138337613723.jpg\" style=\"height:800px; width:800px\" /></p>\r\n\r\n<p><em>Đ&egrave;n lồng đỏ trang tr&iacute; Tết truyền thống thể hiện sự b&igrave;nh y&ecirc;n, hạnh ph&uacute;c</em></p>\r\n\r\n<h3>C&acirc;y quất</h3>\r\n\r\n<p>C&acirc;y quất cũng l&agrave; một trong những loại c&acirc;y cảnh được người Việt đặc biệt l&agrave; những gia đ&igrave;nh miền Bắc ưa chuộng trong ng&agrave;y Tết. Vỏ quất m&agrave;u v&agrave;ng tượng trưng cho sự m&agrave;u mỡ v&agrave; tr&ugrave; ph&uacute;. C&acirc;y quất được b&agrave;y ng&agrave;y Tết c&oacute; c&agrave;ng nhiều quả c&agrave;ng mang &yacute; nghĩa t&agrave;i lộc cho gia đ&igrave;nh.</p>\r\n\r\n<p><img alt=\"trang-tri-tet\" src=\"https://img.homedy.com/store/images/2020/12/25/y-nghia-do-trang-tri-tet-637445138765553424.jpg\" style=\"height:800px; width:800px\" /></p>\r\n\r\n<p><em>Trang tr&iacute; đơn giản mang lại kh&ocirc;ng kh&iacute; Tết v&ocirc; c&ugrave;ng ấm &aacute;p</em></p>\r\n\r\n<p><strong>&gt;&gt; Xem th&ecirc;m:&nbsp;<a href=\"https://homedy.com/post/5fe59cafa9ee1237347a32eb\">Trang tr&iacute; Tết cho ph&ograve;ng nhỏ</a></strong></p>\r\n\r\n<h2>2. &Yacute; tưởng trang tr&iacute; nh&agrave; Tết h&uacute;t t&agrave;i lộc</h2>\r\n\r\n<h3>Trang tr&iacute; nh&agrave; bằng v&ograve;ng hoa Tết</h3>\r\n\r\n<p>Kh&ocirc;ng chỉ gi&aacute;ng sinh mới c&oacute; v&ograve;ng nguyệt quế để treo trước cửa nh&agrave;, việc trang tr&iacute; ng&ocirc;i nh&agrave; của bạn bằng những v&ograve;ng hoa Tết thế n&agrave;y cũng sẽ l&agrave;m ng&ocirc;i nh&agrave; của bạn rực rỡ hơn rất nhiều.</p>\r\n\r\n<p><img alt=\"trang-tri-tet\" src=\"https://img.homedy.com/store/images/2020/12/25/vong-hoa-tet-637445139290640816.jpg\" style=\"height:800px; width:800px\" /></p>\r\n\r\n<p><em>V&ograve;ng hoa tết treo trước cửa</em></p>\r\n\r\n<h3>Trang tr&iacute; nh&agrave; bằng c&acirc;y đ&aacute; qu&yacute; theo phong thủy</h3>\r\n\r\n<p>Đối với những gia đ&igrave;nh đặc biệt quan t&acirc;m đến phong thủy th&igrave; việc sở hữu ri&ecirc;ng một c&acirc;y đ&aacute; qu&yacute; để b&agrave;n trong ng&agrave;y Tết l&agrave; việc kh&ocirc;ng thể bỏ qua. Với thiết kế nhỏ gọn, sang trọng, m&agrave;u sắc đa dạng bắt mắt th&igrave; đ&acirc;y được coi l&agrave; vật dụng trang tr&iacute; rất ph&ugrave; hợp trong ng&agrave;y tết truyền thống.</p>\r\n\r\n<p><img alt=\"trang-tri-tet\" src=\"https://img.homedy.com/store/images/2020/12/25/cay-phong-thuy-ta-loc-637445139718496812.jpg\" style=\"height:800px; width:800px\" /></p>\r\n\r\n<p><em>C&acirc;y t&agrave;i lộc c&oacute; khả năng mang lại ph&uacute; qu&yacute; cho gia chủ</em></p>\r\n\r\n<h3>Trang tr&iacute; nh&agrave; Tết theo phong c&aacute;ch Nhật Bản</h3>\r\n\r\n<p>Nếu bạn muốn tạo ra sự kh&aacute;c biệt trong Tết năm nay th&igrave; c&oacute; thể tham khảo c&aacute;ch trang tr&iacute; nh&agrave; Tết bằng quạt giấy đỏ theo phong c&aacute;ch Nhật Bản dưới đ&acirc;y:</p>\r\n\r\n<p><img alt=\"trang-tri-tet\" src=\"https://img.homedy.com/store/images/2020/12/25/trang-tri-tet-bang-quat-do-637445139999898190.jpg\" style=\"height:1200px; width:800px\" /></p>\r\n\r\n<p><em>Trang tr&iacute; nh&agrave; Tết độc đ&aacute;o treo phong c&aacute;ch Nhật Bản</em></p>\r\n\r\n<h3>Trang tr&iacute; nh&agrave; Tết thủ c&ocirc;ng</h3>\r\n\r\n<p>Đối với gia chủ c&oacute; một đ&ocirc;i tay kh&eacute;o l&eacute;o th&igrave; việc trang tr&iacute; nh&agrave; Tết cũng kh&ocirc;ng cần qu&aacute; phụ thuộc v&agrave;o những vật dụng c&oacute; sẵn. Tự tay cắt ra những b&ocirc;ng hoa đ&agrave;o rực rỡ từ giấy m&agrave;u để trang tr&iacute; cho ng&ocirc;i nh&agrave; của m&igrave;nh cũng l&agrave; một &yacute; tưởng trang tr&iacute; đẹp, độc đ&aacute;o m&agrave; lại v&ocirc; c&ugrave;ng tiết kiệm chi ph&iacute;.</p>\r\n\r\n<p><img alt=\"trang-tri-tet\" src=\"https://img.homedy.com/store/images/2020/12/25/1-637445140747305391.jpg\" style=\"height:1051px; width:800px\" /></p>\r\n\r\n<p><em>Tự tay trang tr&iacute; nh&agrave; Tết bằng hoa giấy thủ c&ocirc;ng</em></p>\r\n\r\n<h3>Trang tr&iacute; nh&agrave; bằng linh vật phong thủy</h3>\r\n\r\n<p>Để ch&agrave;o đ&oacute;n năm T&acirc;n Sửu v&agrave; mang lại may mắn cho gia đ&igrave;nh th&igrave; gia chủ c&oacute; thể lựa chọn một bức tượng hoặc tranh linh vật phong thủy. Linh vật phong thủy của năm 2021 l&agrave; tr&acirc;u v&agrave;ng, do đ&oacute; bạn c&oacute; thể đặt những bức tượng tr&acirc;u v&agrave;ng trong ph&ograve;ng kh&aacute;ch, ph&ograve;ng l&agrave;m việc để mang đến sự gi&agrave;u c&oacute; v&agrave; sung t&uacute;c cho gia đ&igrave;nh.</p>\r\n\r\n<p><img alt=\"trang-tri-tet\" src=\"https://img.homedy.com/store/images/2020/12/25/tuong-linh-vat-trau-ma-vang-637445141150513240.jpg\" style=\"height:488px; width:800px\" /></p>\r\n\r\n<p><em>Tr&acirc;u v&agrave;ng t&agrave;i lộc tượng trưng cho sự gi&agrave;u c&oacute;, sung t&uacute;c</em></p>\r\n\r\n<p>Sử dụng tranh treo tường phong thủy cũng l&agrave; một &yacute; tưởng trang tr&iacute; nh&agrave; chưa bao giờ lỗi &ldquo;mốt&rdquo;. Năm con tr&acirc;u bạn cũng c&oacute; thể lấy bức tranh h&igrave;nh con tr&acirc;u l&agrave;m điểm nhấn cho căn ph&ograve;ng của bạn.</p>\r\n\r\n<p><img alt=\"trang-tri-tet\" src=\"https://img.homedy.com/store/images/2020/12/25/tranh-treo-tuong-con-trau-637445141461929393.jpg\" style=\"height:600px; width:800px\" /></p>\r\n\r\n<p><em>H&igrave;nh ảnh tranh treo tường con tr&acirc;u khiến cho căn ph&ograve;ng trở n&ecirc;n sinh động</em></p>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave; tổng hợp những&nbsp;<strong>&yacute; tưởng&nbsp;trang tr&iacute; nh&agrave; Tết</strong>&nbsp;đẹp, đơn giản v&agrave; c&oacute; &yacute; nghĩa phong thủy cho gia chủ. Hy vọng bạn đ&atilde; t&igrave;m được những &yacute; tưởng trang tr&iacute; ph&ugrave; hợp để trang ho&agrave;ng lại ng&ocirc;i nh&agrave; của m&igrave;nh chuẩn bị cho một năm mới sắp tới.</p>', '0d5310f57ce980b7d9f817_1596879786.jpg', '2021-01-08', 'admin1', NULL, '2021-01-08 11:31:02'),
(7, '9+ đồ trang trí Tết không thể thiếu chào đón năm Tân Sửu 2021 phát lộc, phát tài', 'Mỗi năm, dịp Tết đến xuân về luôn là thời điểm thích hợp để người người nhà nhà sửa sang nhà cửa trang hoàng đón Tết. Trang trí Tết không chỉ giúp làm đẹp cho ngôi nhà mà nó còn chứa đựng mong muốn mang đến may mắn, tài lộc cho gia chủ. Hãy cùng Homedy tìm hiểu 9+ những món đồ trang trí Tết không thể thiếu trong dịp Tết Tân Sửu 2021 này qua bài viết dưới đây nhé!', '<h2>&Yacute; nghĩa trang tr&iacute; Tết trong nh&agrave;</h2>\r\n\r\n<p>Theo d&acirc;n gian, dọn dẹp nh&agrave; cửa đ&oacute;n Tết l&agrave; để &ldquo;đuổi&rdquo; những c&aacute;i cũ kỹ, xấu x&iacute;, kh&ocirc;ng may mắn của năm cũ&nbsp;đi, chuẩn bị đ&oacute;n nhận những điều đẹp đẽ nhất của năm mới về. B&ecirc;n cạnh đ&oacute;, Tết cũng l&agrave; dịp để mọi người đến thăm nh&agrave; nhau, d&agrave;nh tặng nhau những lời ch&uacute;c tụng năm mới. V&igrave; vậy, một ng&ocirc;i nh&agrave; đẹp, sạch sẽ trong dịp Tết l&agrave; điều v&ocirc; c&ugrave;ng quan trọng với mọi gia đ&igrave;nh.</p>\r\n\r\n<h2>9+ những m&oacute;n đồ trang tr&iacute; Tết kh&ocirc;ng thể thiếu trong dịp Tết T&acirc;n Sửu 2021</h2>\r\n\r\n<p>Nhắc đến Tết, ch&uacute;ng ta kh&ocirc;ng thể kh&ocirc;ng nhắc đến những m&oacute;n đồ trang tr&iacute; t&ocirc; điểm th&ecirc;m sắc xu&acirc;n sau:</p>\r\n\r\n<h3>Hoa đ&agrave;o, hoa mai</h3>\r\n\r\n<p>Từ xưa đến nay, việc trưng những c&agrave;nh đ&agrave;o, mai trong nh&agrave; dịp Tết đ&atilde; l&agrave; một truyền thống quen thuộc của người Việt. Bởi kh&ocirc;ng chỉ mang lại m&agrave;u sắc tươi tắn, trang nh&atilde; cho căn nh&agrave;, mỗi lo&agrave;i hoa c&ograve;n mang một &yacute; nghĩa ri&ecirc;ng của n&oacute;.</p>\r\n\r\n<p>Nếu hoa đ&agrave;o được coi l&agrave; tinh hoa của Ngũ h&agrave;nh, c&oacute; thể xua đuổi b&aacute;ch quỷ v&agrave; mang đến cuộc sống b&igrave;nh an, hạnh ph&uacute;c; th&igrave; hoa mai biểu trưng cho sự gi&agrave;u sang, ph&uacute; qu&yacute;, niềm vui, hạnh ph&uacute;c v&agrave; sự gắn kết. V&igrave; vậy, bạn h&atilde;y chuẩn bị cho ng&ocirc;i nh&agrave; của m&igrave;nh những c&agrave;nh đ&agrave;o, c&agrave;nh mai tươi thắm trong dịp Tết để mang sắc xu&acirc;n v&agrave;o nh&agrave; nh&eacute;!</p>\r\n\r\n<p><img alt=\"hoa-dao\" src=\"https://img.homedy.com/store/images/2020/12/25/lg-2019-04-26-hoa-dao-1-637444809503444787.jpg\" style=\"height:453px; width:800px\" /></p>\r\n\r\n<p><em>Hoa đ&agrave;o l&agrave; lo&agrave;i hoa kh&ocirc;ng thể thiếu được trong ng&agrave;y Tết cổ truyền</em></p>\r\n\r\n<p><strong>&gt;&gt;&gt; Xem full b&agrave;i viết:&nbsp;<a href=\"https://homedy.com/post/5fe3fcf9ca7d5e41f454bea7\">Trang tr&iacute; hoa Đ&agrave;o dịp Tết</a></strong></p>\r\n\r\n<p><img alt=\"hoa-mai\" src=\"https://img.homedy.com/store/images/2020/12/25/tai-sao-hoa-dao-chua-duoc-1579448091-637444809818757665.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>Hoa mai quen thuộc với người Việt dịp Tết bởi những &yacute; nghĩa tốt l&agrave;nh của n&oacute;</em></p>\r\n\r\n<h3>C&acirc;u đối</h3>\r\n\r\n<p>D&acirc;n gian c&oacute; c&acirc;u: &ldquo;Thịt mỡ dưa h&agrave;nh c&acirc;u đối đỏ&rdquo; - &yacute; chỉ những m&oacute;n đồ kh&ocirc;ng thể thiếu trong mỗi dịp Tết đến xu&acirc;n về. Theo quan niệm d&acirc;n gian, c&acirc;u đối kh&ocirc;ng chỉ l&agrave; n&eacute;t văn h&oacute;a truyền thống của người Việt m&agrave; c&ograve;n mang h&agrave;m &yacute; mang lại may mắn cho gia đ&igrave;nh.&nbsp;</p>\r\n\r\n<p>C&acirc;u đối đỏ đồng thời cũng l&agrave; những lời ch&uacute;c tốt l&agrave;nh v&agrave; những niềm tin, hy vọng m&agrave; c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh mong muốn. V&igrave; thế, ng&agrave;y nay d&ugrave; cuộc sống c&oacute; hiện đại hơn, nhưng những c&acirc;u đối mang d&aacute;ng vẻ truyền thống, lễ nghi vẫn l&agrave; m&oacute;n đồ trang tr&iacute; quen thuộc trong mỗi gia đ&igrave;nh.</p>\r\n\r\n<p><img alt=\"cau-doi\" src=\"https://img.homedy.com/store/images/2020/12/25/1-637445030318834884.jpg\" style=\"height:450px; width:800px\" /></p>\r\n\r\n<p><em>C&acirc;u đối l&agrave; m&oacute;n đồ trang tr&iacute; Tết kh&ocirc;ng thể thiếu với mọi nh&agrave;</em></p>\r\n\r\n<p><img alt=\"cau-doi\" src=\"https://img.homedy.com/store/images/2020/12/25/cau-doi-tet-1-637444810438034613.jpg\" style=\"height:533px; width:800px\" /></p>\r\n\r\n<p><em>C&acirc;u đối mang những &yacute; nghĩa tốt l&agrave;nh, tốt đẹp cho gia đ&igrave;nh</em></p>\r\n\r\n<p><strong>&gt;&gt;&gt; Xem th&ecirc;m b&agrave;i viết:&nbsp;<a href=\"https://homedy.com/post/5fe452eaca7d5e41f454cce5\">&Yacute; tưởng trang tr&iacute; nh&agrave; ng&agrave;y Tết</a></strong></p>\r\n\r\n<h3>Bao l&igrave; x&igrave;</h3>\r\n\r\n<p>Trao bao l&igrave; x&igrave; v&agrave;o ng&agrave;y Tết l&agrave; một phong tục tốt đẹp của người Việt, h&agrave;nh động n&agrave;y thể hiện t&igrave;nh cảm, sự&nbsp;y&ecirc;u thương m&agrave; c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh d&agrave;nh tặng cho nhau. B&ecirc;n cạnh &yacute; nghĩa đ&oacute;, ta cũng c&oacute; thể d&ugrave;ng bao l&igrave; x&igrave; như một m&oacute;n đồ trang tr&iacute; đẹp mắt cho ng&ocirc;i nh&agrave; của m&igrave;nh. Khi treo bao l&igrave; x&igrave; l&ecirc;n c&agrave;nh mai, c&agrave;nh đ&agrave;o hay chậu c&acirc;y cảnh,... &yacute; nghĩa may mắn c&agrave;ng th&ecirc;m đủ đầy.</p>\r\n\r\n<p><img alt=\"bao-li-xi\" src=\"https://img.homedy.com/store/images/2020/12/25/2-637445030655232162.jpg\" style=\"height:600px; width:800px\" /></p>\r\n\r\n<p><em>Bao l&igrave; x&igrave; đỏ mang &yacute; nghĩa may mắn, tốt l&agrave;nh</em></p>\r\n\r\n<p><img alt=\"bao-li-xi\" src=\"https://img.homedy.com/store/images/2020/12/25/3-637445030861711199.jpg\" style=\"height:600px; width:800px\" /></p>\r\n\r\n<p><em>Ng&agrave;y nay, bao l&igrave; x&igrave; Tết ng&agrave;y c&agrave;ng được thiết kế hiện đại</em></p>\r\n\r\n<p><strong>&gt;&gt;&gt; Xem full b&agrave;i viết:&nbsp;<a href=\"https://homedy.com/post/5fe44eb6ca7d5e41f454cca2\">Trang tr&iacute; Tết bằng bao l&igrave; x&igrave;</a></strong></p>\r\n\r\n<h3>Tranh treo Tết</h3>\r\n\r\n<p>Treo tranh ng&agrave;y Tết l&agrave; một th&uacute; chơi tao nh&atilde; mỗi dịp Tết đến Xu&acirc;n về. Bạn c&oacute; thể dễ d&agrave;ng t&igrave;m mua những bức tranh Tết ở c&aacute;c cửa h&agrave;ng chuy&ecirc;n về tranh hay c&oacute; khi ở cả những khu chợ Tết tấp nập. Tranh treo Tết ng&agrave;y nay c&oacute; đa dạng về mẫu m&atilde;, h&igrave;nh thức, ph&ugrave; hợp với mọi nhu cầu của kh&aacute;ch. Nếu ưa th&iacute;ch phong c&aacute;ch ho&agrave;i cổ, bạn c&oacute; thể d&ugrave;ng tranh Đ&ocirc;ng Hồ, H&agrave;ng Trống; hoặc nếu l&agrave; người th&iacute;ch vẻ hiện đại, s&aacute;ng tạo, những bức tranh phong cảnh hay hoa cỏ sẽ l&agrave; lựa chọn th&iacute;ch hợp cho bạn!</p>\r\n\r\n<p><img alt=\"tranh-treo-tuong\" src=\"https://img.homedy.com/store/images/2020/12/25/tranh-dong-ho-treo-tuong-2-637444811586945930.jpg\" style=\"height:729px; width:800px\" /></p>\r\n\r\n<p><em>Tranh Đ&ocirc;ng Hồ mang lại cho kh&ocirc;ng gian nh&agrave; bạn n&eacute;t d&acirc;n gian, ho&agrave;i cổ</em></p>\r\n\r\n<p><img alt=\"tranh-treo-tuong\" src=\"https://img.homedy.com/store/images/2020/12/25/tranh-treo-tuong-phong-khach-hoa-dao-tuoi-tham-ghep-bo-4-tam-1400-2-637444811857145777.jpg\" style=\"height:542px; width:800px\" /></p>\r\n\r\n<p><em>Ngo&agrave;i ra, những bức tranh hoa cỏ cũng l&agrave; lựa chọn th&iacute;ch hợp cho tranh treo ng&agrave;y Tết</em></p>\r\n\r\n<p><strong>&gt;&gt;&gt; Xem th&ecirc;m:&nbsp;<a href=\"https://homedy.com/post/5fe44eb6ca7d5e41f454cca2\">&Yacute; tưởng trang tr&iacute; ph&ograve;ng kh&aacute;ch ng&agrave;y Tết</a></strong></p>\r\n\r\n<h3>Đ&egrave;n trang tr&iacute;</h3>\r\n\r\n<p>Những chiếc đ&egrave;n lồng hay d&acirc;y đ&egrave;n trang tr&iacute; nhấp nh&aacute;y sẽ khiến căn nh&agrave; bạn trở n&ecirc;n lung linh hơn bao giờ hết. M&agrave;u sắc rực rỡ, ấm &aacute;p tỏa ra từ đ&egrave;n kết hợp với c&agrave;nh đ&agrave;o hay hoa mai c&agrave;ng l&agrave;m tăng sự ấm c&uacute;ng trong kh&ocirc;ng gian nh&agrave;. Tết n&agrave;y, đừng qu&ecirc;n trang tr&iacute; những d&acirc;y đ&egrave;n đầy m&agrave;u sắc để căn nh&agrave; trở n&ecirc;n lấp l&aacute;nh hơn bạn nh&eacute;!</p>\r\n\r\n<p><img alt=\"den-trang-tri\" src=\"https://img.homedy.com/store/images/2020/12/25/lam-do-trang-tri-tet-handmade-14-637444812226935919.jpg\" style=\"height:449px; width:800px\" /></p>\r\n\r\n<p><em>Đ&egrave;n lồng l&agrave; m&oacute;n đồ trang tr&iacute; quen thuộc dịp Tết</em></p>\r\n\r\n<p><img alt=\"den-trang-tri\" src=\"https://lh4.googleusercontent.com/HJUWDvfhiKGdJm9fuKswLt2Mi12J9tiT70WzyScfz5Nt1tva94UMi7Cg2uK2tozxCGeX-vmrzox9CMR1Y5oxjOC9wH9e2-wzMkkXprKfVttazbKqqbMIE8pUT3SNSa8-O4FJuitY\" style=\"height:597px; width:800px\" /></p>\r\n\r\n<p><em>Chỉ một v&agrave;i d&acirc;y đ&egrave;n th&ocirc;i cũng đủ khiến cả căn nh&agrave; bừng s&aacute;ng</em></p>\r\n\r\n<p><strong>&gt;&gt;&gt; Xem th&ecirc;m:&nbsp;<a href=\"https://homedy.com/post/5fe452eaca7d5e41f454cce5\">&Yacute; tưởng trang tr&iacute; nh&agrave; ng&agrave;y Tết</a></strong></p>\r\n\r\n<h3>Đồ gốm</h3>\r\n\r\n<p>D&acirc;n gian c&oacute; c&acirc;u:&rdquo;Nhất chữ, nh&igrave; tranh, tam s&agrave;nh, tứ mộc&rdquo; mang h&agrave;m &yacute; n&oacute;i đến những th&uacute; chơi tao nh&atilde; của người Việt trong mỗi dịp Tết, trong đ&oacute;, &ldquo;s&agrave;nh&rdquo; &yacute; chỉ đến gốm. Gốm l&agrave; m&oacute;n đồ trang tr&iacute; sang trọng, thanh tao lại kh&ocirc;ng k&eacute;m phần truyền thống, cổ điển. Đ&acirc;y l&agrave; lựa chọn th&iacute;ch hợp cho ng&agrave;y Xu&acirc;n kh&ocirc;ng chỉ bởi yếu tố kiểu d&aacute;ng m&agrave; c&ograve;n nằm ở &yacute; nghĩa của n&oacute;.</p>\r\n\r\n<p>C&aacute;c m&oacute;n đồ trang tr&iacute; l&agrave;m bằng gốm thường c&oacute; họa tiết tinh xảo, l&agrave; sự kết hợp uyển chuyển, h&agrave;i h&ograve;a của những đường n&eacute;t kh&aacute;c nhau tạo n&ecirc;n những hoa văn tinh tế. Sự kết hợp h&agrave;i h&ograve;a ấy biểu trưng cho n&eacute;t h&agrave;i h&ograve;a của &Acirc;m - Dương ngũ h&agrave;nh, mang lại sự may mắn, hanh th&ocirc;ng cho gia chủ trong năm mới.</p>\r\n\r\n<p><img alt=\"trang-tri-tet\" src=\"https://img.homedy.com/store/images/2020/12/25/4-637445031961911214.jpg\" style=\"height:1067px; width:800px\" /></p>\r\n\r\n<p><em>Gốm l&agrave; th&uacute; chơi tao nh&atilde; mỗi dịp Tết đến Xu&acirc;n về</em></p>\r\n\r\n<p><img alt=\"trang-tri-tet\" src=\"https://img.homedy.com/store/images/2020/12/25/5-637445032176979166.jpg\" style=\"height:1067px; width:800px\" /></p>\r\n\r\n<p><em>Những b&igrave;nh gốm mang lại n&eacute;t truyền thống, cổ điển rất ri&ecirc;ng của văn h&oacute;a Việt Nam</em></p>\r\n\r\n<p><strong>&gt;&gt;&gt; Xem th&ecirc;m:&nbsp;<a href=\"https://homedy.com/post/5fe455fcca7d5e41f454cd29\">Trang tr&iacute; Tết sử dụng đồ gốm</a></strong></p>\r\n\r\n<h3>C&aacute;c loại d&acirc;y may mắn</h3>\r\n\r\n<p>D&acirc;y may mắn l&agrave; m&oacute;n đồ trang tr&iacute; Tết quen thuộc với mỗi gia đ&igrave;nh Việt. M&oacute;n đồ trang tr&iacute; n&agrave;y thu h&uacute;t mọi người bởi kiểu d&aacute;ng đẹp đẽ, m&agrave;u sắc rực rỡ, nhỏ xinh m&agrave; v&ocirc; c&ugrave;ng tiết kiệm chi ph&iacute;. Bạn c&oacute; thể dễ d&agrave;ng t&igrave;m mua c&aacute;c loại d&acirc;y treo may mắn dịp Tết n&agrave;y ở bất cứ cửa h&agrave;ng b&aacute;n đồ Tết n&agrave;o với mức gi&aacute; rất phải chăng.</p>\r\n\r\n<p>Với m&agrave;u sắc tươi tắn, rực rỡ k&egrave;m những th&ocirc;ng điệp &yacute; nghĩa như sợi chỉ đỏ, n&eacute;n v&agrave;ng, đồng tiền khắc k&egrave;m những c&acirc;u từ mang h&agrave;m &yacute; cầu may mắn, ph&aacute;t t&agrave;i; d&acirc;y may mắn chắc chắn l&agrave; một m&oacute;n đồ trang tr&iacute; kh&ocirc;ng thể thiếu trong nh&agrave; bạn mỗi dịp Tết đến Xu&acirc;n về!</p>\r\n\r\n<p><img alt=\"do-trang-tri-tet\" src=\"https://img.homedy.com/store/images/2020/12/25/6-637445032331105423.jpg\" style=\"height:534px; width:800px\" /></p>\r\n\r\n<p><em>Những sợi d&acirc;y may mắn đầy m&agrave;u sắc, mang lại kh&ocirc;ng kh&iacute; đậm vị Tết</em></p>\r\n\r\n<p><strong>&gt;&gt;&gt; Xem th&ecirc;m:&nbsp;<a href=\"https://homedy.com/post/5fe452eaca7d5e41f454cce5\">&Yacute; tưởng trang tr&iacute; ph&ograve;ng kh&aacute;ch ng&agrave;y Tết</a></strong></p>\r\n\r\n<h3>C&acirc;y cảnh</h3>\r\n\r\n<p>Nếu bạn ưa th&iacute;ch m&agrave;u xanh tươi non v&agrave; tr&agrave;n trề sức sống, c&aacute;c loại c&acirc;y cảnh sẽ l&agrave; lựa chọn v&ocirc; c&ugrave;ng th&iacute;ch hợp cho kh&ocirc;ng gian nh&agrave; bạn trong những ng&agrave;y Tết cổ truyền. C&acirc;y cảnh mang đến cho căn nh&agrave; kh&ocirc;ng kh&iacute; tươi mới, trong l&agrave;nh v&agrave; căng tr&agrave;n nhựa sống.&nbsp;</p>\r\n\r\n<p>Một số lựa chọn th&iacute;ch hợp trong ng&agrave;y Tết c&oacute; thể kể đến như c&acirc;y xanh, c&acirc;y sung, c&acirc;y quất với những thế c&acirc;y được uốn lượn đầy nghệ thuật. Những c&acirc;y cảnh với lộc xanh mơn mởn v&agrave; hoa tr&aacute;i xum xu&ecirc; biểu thị cho sự ph&aacute;t t&agrave;i, ph&aacute;t lộc, sinh s&ocirc;i nảy nở, t&agrave;i lộc v&agrave; may mắn cho gia chủ.</p>\r\n\r\n<p><img alt=\"trang-tri-tet\" src=\"https://img.homedy.com/store/images/2020/12/25/quat3-12502530-637444815515685349.jpg\" style=\"height:1163px; width:800px\" /></p>\r\n\r\n<p><em>C&acirc;y quất xum xu&ecirc; tr&aacute;i mang &yacute; nghĩa sinh s&ocirc;i nảy nở, ph&aacute;t triển, thịnh vượng</em></p>\r\n\r\n<p><strong>&gt;&gt;&gt; Xem th&ecirc;m:&nbsp;<a href=\"https://homedy.com/post/5fe452eaca7d5e41f454cce5\">&Yacute; tưởng trang tr&iacute; nh&agrave; ng&agrave;y Tết</a></strong></p>\r\n\r\n<h3>C&aacute;c loại hoa giả</h3>\r\n\r\n<p>Hoa giả l&agrave; một lựa chọn đ&aacute;ng để t&acirc;m cho những m&oacute;n đồ trang tr&iacute; ng&agrave;y Tết bởi m&agrave;u sắc đẹp đẽ, sự tiện dụng v&agrave; khả năng bền cao của n&oacute;. Bạn c&oacute; thể thoải m&aacute;i trang tr&iacute; hoa giả trong nhiều ng&agrave;y hoặc ở những nơi c&oacute; nhiệt độ cao, kh&oacute; c&oacute; thể để được hoa tươi m&agrave; kh&ocirc;ng lo ch&uacute;ng bị hư hay h&eacute;o. Ng&agrave;y nay với tr&igrave;nh độ c&ocirc;ng nghệ ti&ecirc;n tiến, c&aacute;c loại hoa giả cũng được thiết kế tinh xảo hơn với đa dạng m&agrave;u sắc, kiểu d&aacute;ng, tr&ocirc;ng giống thật đến nỗi nếu kh&ocirc;ng nh&igrave;n gần hay sờ v&agrave;o th&igrave; rất kh&oacute; m&agrave; ph&aacute;t hiện ra l&agrave; giả.</p>\r\n\r\n<p><img alt=\"trang-tri-tet\" src=\"https://img.homedy.com/store/images/2020/12/25/cay-dao-hoa-man-9-2-637444815863180443.jpg\" style=\"height:669px; width:800px\" /></p>\r\n\r\n<p><em>Hoa giả được thiết kế với đa dạng m&agrave;u sắc, kiểu d&aacute;ng</em></p>\r\n\r\n<p><strong>&gt;&gt;&gt; Xem th&ecirc;m:&nbsp;<a href=\"https://homedy.com/post/5fe452eaca7d5e41f454cce5\">&Yacute; tưởng trang tr&iacute; nh&agrave; ng&agrave;y Tết</a></strong></p>\r\n\r\n<p>Hy vọng những gợi &yacute; tr&ecirc;n đ&acirc;y sẽ gi&uacute;p c&aacute;c bạn t&igrave;m được những&nbsp;<strong>m&oacute;n đồ trang tr&iacute; dịp Tết</strong>&nbsp;ưng &yacute;. Mỗi năm Tết chỉ đến một lần, đừng ngần ngại m&agrave; h&atilde;y trang ho&agrave;ng ng&ocirc;i nh&agrave; m&igrave;nh thật đầy m&agrave;u sắc ấm &aacute;p bạn nh&eacute;. Homedy ch&uacute;c bạn v&agrave; gia đ&igrave;nh c&oacute; một Tết T&acirc;n Sửu an l&agrave;nh, hạnh ph&uacute;c!</p>', 'hinh50.png', '2021-01-07', 'admin', NULL, '2021-01-08 03:15:06'),
(8, '7 phong cách trang trí mùa NOEL hot nhất 2020', 'Giáng Sinh đã cận kề rồi, bạn có tò mò cách “biến hóa” căn nhà ngập tràn không khí Noel nhưng vẫn giữ được phong cách của riêng mình? Hãy để Homedy mách bạn cách trang trí Noel đơn giản, không tốn kém mà vẫn siêu ấn tượng cho 7 phong cách nhà khác nhau qua bài viết dưới đây nhé!', '<h2>&Yacute; nghĩa trang tr&iacute; Noel trong nh&agrave;</h2>\r\n\r\n<p>Lễ Gi&aacute;ng Sinh c&oacute; nguồn gốc từ phương T&acirc;y, l&agrave; ng&agrave;y lễ kỷ niệm Ch&uacute;a Gi&ecirc;-su ra đời. Ở Việt Nam, tuy kh&ocirc;ng phải l&agrave; một ng&agrave;y lễ ch&iacute;nh thức nhưng dần dần Noel đ&atilde; trở th&agrave;nh dịp để cả gia đ&igrave;nh c&oacute; cơ hội qu&acirc;y quần b&ecirc;n nhau, tr&ograve; chuyện t&acirc;m sự v&agrave; trao cho nhau những m&oacute;n qu&agrave;, những lời ch&uacute;c t&igrave;nh cảm đầy &yacute; nghĩa. Đ&acirc;y cũng l&agrave; dịp để bạn b&egrave; c&oacute; thể tụ họp h&agrave;n huy&ecirc;n, c&ugrave;ng kể cho nhau nghe những c&acirc;u chuyện sau một năm d&agrave;i.</p>\r\n\r\n<p>V&agrave; những c&acirc;u chuyện hẳn sẽ th&ecirc;m phần thi vị hơn khi được kể dưới &aacute;nh đ&egrave;n lung linh tr&ecirc;n những c&acirc;y th&ocirc;ng đầy m&agrave;u sắc, trong kh&ocirc;ng gian Gi&aacute;ng Sinh ấm &aacute;p. Kh&ocirc;ng chỉ vậy, việc trang tr&iacute; Noel theo từng phong c&aacute;ch kh&aacute;c nhau c&ograve;n gi&uacute;p thể hiện r&otilde; hơn t&iacute;nh c&aacute;ch của gia chủ.</p>\r\n\r\n<h2>7 phong c&aacute;ch trang tr&iacute; Noel đơn giản si&ecirc;u ấn tượng&nbsp;</h2>\r\n\r\n<h3>Phong c&aacute;ch Vintage</h3>\r\n\r\n<p>Vintage l&agrave; phong c&aacute;ch thiết kế đ&atilde; v&agrave; đang được y&ecirc;u th&iacute;ch tr&ecirc;n to&agrave;n thế giới. Từ những n&eacute;t đẹp trong qu&aacute; khứ, n&oacute; đ&atilde; đem lại một hơi thở mới cho cuộc sống hiện đại với sự ho&agrave;i cổ, sang trọng v&agrave; l&atilde;ng mạn. Phong c&aacute;ch Vintage l&agrave; sự lựa chọn tối ưu cho những ai y&ecirc;u th&iacute;ch vẻ đẹp dịu d&agrave;ng, mềm mại nhưng kh&ocirc;ng k&eacute;m phần thời trang, ấn tượng.&nbsp;</p>\r\n\r\n<p>Với phong c&aacute;ch n&agrave;y, bạn chỉ cần chuẩn bị một c&acirc;y th&ocirc;ng Noel được trang tr&iacute; với lấp l&aacute;nh những quả th&ocirc;ng, tuần lộc, những quả b&oacute;ng tr&ograve;n với dải d&acirc;y đ&egrave;n nhỏ quấn quanh th&acirc;n c&acirc;y, c&ugrave;ng một ng&ocirc;i sao đặt ngay ngắn tr&ecirc;n ch&iacute;nh giữa c&acirc;y th&ocirc;ng. Th&ecirc;m v&agrave;o đ&oacute; l&agrave; một chiếc l&ograve; sưởi nhỏ v&agrave; một v&agrave;i m&oacute;n đồ trang tr&iacute; tr&ecirc;n tường như quả cầu tuyết, khung gỗ,... Kh&ocirc;ng cần cầu k&igrave; m&agrave; vẫn to&aacute;t l&ecirc;n vẻ sang trọng, ho&agrave;i cổ đ&uacute;ng kh&ocirc;ng n&agrave;o?</p>\r\n\r\n<p><img alt=\"vintage\" src=\"https://img.homedy.com/store/images/2020/12/21/vintage-637441348598008379.jpg\" style=\"height:796px; width:1000px\" /></p>\r\n\r\n<p><em>Trang tr&iacute; Noel phong c&aacute;ch Vintage</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"Vintage\" src=\"https://img.homedy.com/store/images/2020/12/21/trang-tri-noel-phong-cach-vintage-637441349065268957.jpg\" style=\"height:750px; width:1000px\" /></p>\r\n\r\n<p>Phong c&aacute;ch Vintage mang đến kh&ocirc;ng gian nhẹ nh&agrave;ng, mềm mại</p>\r\n\r\n<p><strong>&gt;&gt;&gt; Xem full b&agrave;i viết:</strong>&nbsp;<strong><a href=\"https://homedy.com/post/5fd2d9a12c8cd63380adc5ab\">Phong c&aacute;ch Vintage</a></strong></p>\r\n\r\n<h3>Phong c&aacute;ch t&acirc;n cổ điển ấm c&uacute;ng</h3>\r\n\r\n<p>L&agrave; sự kết hợp giữa phong c&aacute;ch hiện đại với cổ điển, những thiết kế phong c&aacute;ch t&acirc;n cổ điển mang đến sự tinh tế, sang trọng, cầu kỳ bởi sự tỉ mỉ trong từng đường n&eacute;t v&agrave; t&iacute;nh nghệ thuật cao. Với những ng&ocirc;i nh&agrave; mang phong c&aacute;ch T&acirc;n cổ điển ấm c&uacute;ng, c&aacute;c phụ kiện trang tr&iacute; Noel n&ecirc;n l&agrave; những m&oacute;n trang tr&iacute; với tone ấm như m&agrave;u be, kem.&nbsp;</p>\r\n\r\n<p>Bạn c&oacute; thể chọn dải đ&egrave;n trang tr&iacute; quanh c&acirc;y th&ocirc;ng Noel l&agrave; những dải với loại b&oacute;ng đ&egrave;n tr&ograve;n, to, tỏa &aacute;nh s&aacute;ng v&agrave;ng cam nhằm tăng th&ecirc;m phần ấm &aacute;p cho ng&ocirc;i nh&agrave;. B&ecirc;n cạnh đ&oacute;, bạn n&ecirc;n trang tr&iacute; đồ vừa đủ, kh&ocirc;ng n&ecirc;n qu&aacute; cầu kỳ bởi thiết kế trong ng&ocirc;i nh&agrave; theo phong c&aacute;ch T&acirc;n cổ điển đ&atilde; rất tỉ mỉ rồi.</p>\r\n\r\n<p><img alt=\"phong-cach-tan-co-dien\" src=\"https://img.homedy.com/store/images/2020/12/21/phong-cach-637441350569496444.jpg\" style=\"height:750px; width:1000px\" /></p>\r\n\r\n<p><em>Trang tr&iacute; nh&agrave; phong c&aacute;ch t&acirc;n cổ điển ấm c&uacute;ng</em></p>\r\n\r\n<p><img alt=\"phong-cach-tan-co-dien\" src=\"https://img.homedy.com/store/images/2020/12/21/phong-cach-tan-co-dien-637441352077933312.jpg\" style=\"height:750px; width:1000px\" /></p>\r\n\r\n<p><em>Phong c&aacute;ch t&acirc;n cổ điển sang trọng, ấn tượng</em></p>\r\n\r\n<p><strong>&gt;&gt;&gt; Xem full b&agrave;i viết:&nbsp;<a href=\"https://homedy.com/post/5fd2e04c2c8cd63380adc697\">Phong c&aacute;ch T&acirc;n cổ điển ấm c&uacute;ng</a></strong></p>\r\n\r\n<h3>Phong c&aacute;ch Ch&acirc;u &Acirc;u hiện đại</h3>\r\n\r\n<p>Đ&acirc;y l&agrave; phong c&aacute;ch trang tr&iacute; Noel kh&ocirc;ng thể tuyệt hơn d&agrave;nh cho những bạn đam m&ecirc; sự tinh tế, hiện đại. C&acirc;y th&ocirc;ng Noel l&agrave; một m&oacute;n trang tr&iacute; kh&ocirc;ng thể thiếu trong dịp Noel. Những tr&aacute;i b&oacute;ng tr&ograve;n đầy m&agrave;u sắc c&ugrave;ng những quả th&ocirc;ng, tuần lộc,... treo tr&ecirc;n c&acirc;y sẽ l&agrave; điểm nhấn nổi bật cho căn nh&agrave; của bạn. Ngo&agrave;i ra c&oacute; thể sử dụng th&ecirc;m những chiếc vỏ gối m&agrave;u đỏ tr&ecirc;n sofa để tăng th&ecirc;m phần rực rỡ cho ng&ocirc;i nh&agrave; nữa đ&oacute;.&nbsp;</p>\r\n\r\n<p><img alt=\"phong-cach-chau-au\" src=\"https://img.homedy.com/store/images/2020/12/21/phong-cach-chau-au-637441353181542052.jpg\" style=\"height:1333px; width:1000px\" /></p>\r\n\r\n<p><em>Trang tr&iacute; Noel đẹp đơn giản phong c&aacute;ch Ch&acirc;u &Acirc;u hiện đại</em></p>\r\n\r\n<p>Đừng qu&ecirc;n đặt th&ecirc;m v&agrave;i m&oacute;n trang tr&iacute; Noel như mấy c&acirc;y nến trắng, một v&agrave;i nh&aacute;nh th&ocirc;ng, kẹo ngọt, d&acirc;y chữ trang tr&iacute; tr&ecirc;n b&agrave;n ăn v&agrave; một chiếc tất xinh tr&ecirc;n cửa ra v&agrave;o bạn nh&eacute;!</p>\r\n\r\n<p><img alt=\"phong-cach-chau-au-1\" src=\"https://img.homedy.com/store/images/2020/12/21/phong-cach-chau-au-1-637441354280335294.jpg\" style=\"height:1333px; width:1000px\" /></p>\r\n\r\n<p><em>Khu vực b&agrave;n ăn tr&agrave;n ngập kh&ocirc;ng kh&iacute; Noel</em></p>\r\n\r\n<p><strong>&gt;&gt;&gt; Xem full b&agrave;i viết:&nbsp;<a href=\"https://homedy.com/post/5fd31d472c8cd63380adcd48\">Phong c&aacute;ch Ch&acirc;u &Acirc;u hiện đại</a></strong></p>\r\n\r\n<h3>Phong c&aacute;ch Bắc &Acirc;u</h3>\r\n\r\n<p>Phong c&aacute;ch Bắc &Acirc;u được biết đến với 3 đặc điểm nổi bật: đơn giản trong thiết kế, tinh tế trong cảm gi&aacute;c v&agrave; tiện nghi khi sử dụng. Với phong c&aacute;ch sử dụng những gam m&agrave;u tone lạnh, thiết kế đơn giản tinh tế trong nội thất như phong c&aacute;ch Bắc &Acirc;u, m&ugrave;a Noel ch&iacute;nh l&agrave; thời gian th&iacute;ch hợp để &ldquo;biến h&oacute;a&rdquo; căn nh&agrave; bạn trở n&ecirc;n ấm c&uacute;ng, thư gi&atilde;n v&agrave; nhiều m&agrave;u sắc hơn.&nbsp;</p>\r\n\r\n<p>H&atilde;y đặt một c&acirc;y th&ocirc;ng lớn với những tr&aacute;i b&oacute;ng tr&ograve;n, ng&ocirc;i sao v&agrave; dải đ&egrave;n v&agrave;ng m&agrave;u sắc l&agrave;m điểm nhấn. Sử dụng những chiếc v&ograve;ng nguyệt quế v&agrave; nh&aacute;nh th&ocirc;ng đ&iacute;nh tr&ecirc;n tường hoặc trong c&aacute;c vật phẩm trang tr&iacute; trong nh&agrave;, nhớ đặt th&ecirc;m những hộp qu&agrave; dưới ch&acirc;n c&acirc;y th&ocirc;ng Noel nữa bạn nh&eacute;. V&agrave; đừng qu&ecirc;n thắp th&ecirc;m v&agrave;i &aacute;nh nến lung linh trong nh&agrave; gi&uacute;p kh&ocirc;ng gian tăng th&ecirc;m phần ấm c&uacute;ng!</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><img alt=\"phong-cach-bac-au\" src=\"https://img.homedy.com/store/images/2020/12/21/phong-cach-bac-au-637441355333806758.jpg\" style=\"height:666px; width:1000px\" /></p>\r\n\r\n<p><em>Trang tr&iacute; Noel phong c&aacute;ch Bắc &Acirc;u nhẹ nh&agrave;ng, ấn tượng</em></p>\r\n\r\n<p><img alt=\"phong-cach-bac-au\" src=\"https://img.homedy.com/store/images/2020/12/21/phong-cach-bac-au-1-637441356021477429.jpg\" style=\"height:1333px; width:1000px\" /></p>\r\n\r\n<p><em>M&agrave;u sắc trắng - xanh l&agrave; m&agrave;u sắc chủ đạo cho căn hộ đ&oacute;n Noel phong c&aacute;ch Bắc &Acirc;u</em></p>\r\n\r\n<p><strong>&gt;&gt;&gt; Xem full b&agrave;i viết:&nbsp;<a href=\"https://homedy.com/post/5fd326d32c8cd63380adcf5e\">Phong c&aacute;ch Bắc &Acirc;u</a></strong></p>\r\n\r\n<h3>Phong c&aacute;ch Retro</h3>\r\n\r\n<p>Phong c&aacute;ch retro mang những gi&aacute; trị ho&agrave;i cổ, cổ điển xinh đẹp v&agrave; dịu d&agrave;ng. Đ&acirc;y l&agrave; phong c&aacute;ch biểu hiện cho sự ch&acirc;n th&agrave;nh, đơn giản, nhưng kh&ocirc;ng k&eacute;m phần quyến rũ trong cuộc sống hiện đại.&nbsp;</p>\r\n\r\n<p>Mẹo đầu ti&ecirc;n để trang tr&iacute; Noel đơn giản, đẹp m&agrave; vẫn giữ được những n&eacute;t retro đầy dịu d&agrave;ng ch&iacute;nh l&agrave; thay đổi m&agrave;u sắc căn ph&ograve;ng. Xanh lá, đỏ, trắng, bạc, vàng là những gam m&agrave;u đặc trưng cho Noel, bạn có thể dùng bảng màu chuẩn và auto đẹp là 60% trắng / xám / bạc, 30% xanh lá đậm và 10% đỏ cho căn ph&ograve;ng của m&igrave;nh. Những m&agrave;u sắc n&agrave;y c&oacute; thể đến từ những m&oacute;n đồ trang tr&iacute; trong căn ph&ograve;ng hay drap giường, vỏ gối, vỏ chăn.</p>\r\n\r\n<p><img alt=\"phong-cach-retro\" src=\"https://img.homedy.com/store/images/2020/12/21/phong-cach-retro-637441357361892270.jpg\" style=\"height:1334px; width:1000px\" /></p>\r\n\r\n<p><em>Phong c&aacute;ch Retro mang cảm gi&aacute;c ho&agrave;i cổ, nhẹ nh&agrave;ng</em></p>\r\n\r\n<p>Phong c&aacute;ch Retro c&oacute; đặc trưng l&agrave; những bức tranh cổ điển, bạn c&oacute; thể thử thay thế những bức tranh cũ trong nh&agrave; bằng những bức tranh về chủ đề Gi&aacute;ng sinh như bức tranh về những nh&aacute;nh th&ocirc;ng n&agrave;y!</p>\r\n\r\n<p><img alt=\"phong-cach-retro\" src=\"https://img.homedy.com/store/images/2020/12/21/phong-cach-retro-1-637441358081823716.jpg\" style=\"height:1333px; width:1000px\" /></p>\r\n\r\n<p><em>Ph&ograve;ng ngủ phong c&aacute;ch Retro tr&agrave;n ngập sắc m&agrave;u Noel</em></p>\r\n\r\n<p><strong>&gt;&gt;&gt; Xem full b&agrave;i viết:&nbsp;<a href=\"https://homedy.com/post/5fdb1cc25fe4a94238f310c9\">Phong c&aacute;ch Retro</a></strong></p>\r\n\r\n<h3>Phong c&aacute;ch Scandinavian</h3>\r\n\r\n<p>Gi&aacute;ng sinh n&agrave;y, ngo&agrave;i ph&ograve;ng kh&aacute;ch, bạn cũng c&oacute; thể biến tấu cho căn bếp của m&igrave;nh th&ecirc;m nhiều m&agrave;u sắc với những m&oacute;n trang tr&iacute; Noel trong phong c&aacute;ch Scandinavian nhẹ nh&agrave;ng, nh&atilde; nhặn m&agrave; kh&ocirc;ng k&eacute;m phần sang trọng.&nbsp;</p>\r\n\r\n<p>Noel năm nay chuộng sử dụng những loại th&ocirc;ng tươi. Nếu kh&ocirc;ng t&igrave;m được, bạn c&oacute; thể mua một t&uacute;i quả kh&ocirc; (quả th&ocirc;ng, l&aacute;t cam kh&ocirc;, l&aacute; khuynh diệp) c&ugrave;ng quả ch&acirc;u v&agrave; nến thơm, set up một g&oacute;c nhỏ trong nh&agrave; cũng c&oacute; thể mang lại kh&ocirc;ng kh&iacute; Noel. Những tấm thảm đỏ, kẻ caro n&ecirc;n được tận dụng trong những ng&agrave;y n&agrave;y, trải một g&oacute;c b&agrave;n bếp, một g&oacute;c sofa... sẽ gi&uacute;p căn nh&agrave; của bạn mang lại cảm gi&aacute;c ấm &aacute;p đậm vị Gi&aacute;ng Sinh.</p>\r\n\r\n<p><img alt=\"phong-cach-Scandinavian\" src=\"https://img.homedy.com/store/images/2020/12/21/phong-cach-scandinavian-637441360438308033.jpg\" style=\"height:1513px; width:1000px\" /></p>\r\n\r\n<p><em>Trang tr&iacute; Noel phong c&aacute;ch&nbsp;Scandinavian</em></p>\r\n\r\n<p><img alt=\"phong-cach-Scandinavian-1\" src=\"https://img.homedy.com/store/images/2020/12/21/phong-cach-scandinavian-1-637441361064494320.jpg\" style=\"height:668px; width:1000px\" /></p>\r\n\r\n<p><em>Căn ph&ograve;ng ngập tr&agrave;n sắc Noel phong c&aacute;ch&nbsp;Scandinavian</em></p>\r\n\r\n<p><strong>&gt;&gt;&gt; Xem full b&agrave;i viết:&nbsp;<a href=\"https://homedy.com/post/5fdb02715fe4a94238f30c64\">Phong c&aacute;ch Scandinavian</a></strong></p>\r\n\r\n<p><strong>Kết</strong></p>\r\n\r\n<p>Với 7 gợi &yacute; trang tr&iacute; nh&agrave; đơn giản m&agrave; si&ecirc;u ấn tượng trong dịp Noel 2020 n&agrave;y, hy vọng bạn sẽ c&oacute; thể t&igrave;m thấy những cảm hứng trang tr&iacute; Noel cho ng&ocirc;i nh&agrave; m&igrave;nh. Đừng qu&ecirc;n mang một ch&uacute;t hương vị cho tổ ấm của m&igrave;nh v&agrave;o mỗi dịp đặc biệt để đ&aacute;nh dấu v&agrave; tận hưởng cuộc sống tươi đẹp n&agrave;y, bạn nh&eacute;!</p>', 'hinh18.png', '2021-01-07', 'admin', NULL, '2021-01-08 03:12:35'),
(9, '4 nguyên tắc thiết kế ban công nhà ở vừa đẹp vừa hợp phong thủy', 'Ban công nhà ở là không gian giúp đón gió, đón sáng tự nhiên và đảm bảo phong thủy cho nhà ở. Để tận dụng tối đa những công năng đó, gia chủ nên tìm hiểu thiết kế ban công như thế nào cho hợp lý? Hãy cùng Homedy tìm lời giải đáp qua bài viết dưới đây.', '<h2>Tầm quan trọng của thiết kế ban c&ocirc;ng</h2>\r\n\r\n<p>Ban c&ocirc;ng ch&iacute;nh l&agrave; nơi hưởng &aacute;nh s&aacute;ng, kh&iacute; trời trọn vẹn nhất. C&oacute; thể n&oacute;i khu vực n&agrave;y ảnh hưởng kh&ocirc;ng nhỏ tới gia đ&igrave;nh bạn. Nếu kh&eacute;o l&eacute;o trong thiết kế, bạn c&oacute; thể biến kh&ocirc;ng gian th&ocirc; cứng, nh&agrave;m ch&aacute;n th&agrave;nh một khu vườn nhỏ hay nơi để thường thức cuộc sống. Chắc chắn c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh bạn sẽ cảm thấy thư th&aacute;i v&agrave; thoải m&aacute;i hơn đ&oacute;.</p>\r\n\r\n<p>Một ban c&ocirc;ng đẹp sẽ gi&uacute;p kh&ocirc;ng gian sống của gia đ&igrave;nh bạn trở n&ecirc;n ấn tượng v&agrave; thu h&uacute;t hơn. C&oacute; rất nhiều c&aacute;ch để l&agrave;m việc n&agrave;y như cho th&ecirc;m bộ b&agrave;n ghế nhỏ c&ugrave;ng t&aacute;ch tr&agrave; nh&acirc;m nhi, hay trồng những c&acirc;y xanh mang đến cuộc sống thư th&aacute;i... Tuy nhi&ecirc;n, tất cả c&aacute;c c&aacute;ch n&agrave;y cần tu&acirc;n thủ theo những nguy&ecirc;n tắc nhất định.&nbsp;</p>\r\n\r\n<p><img alt=\"bancong\" src=\"https://img.homedy.com/store/images/2020/12/16/bancong-637436882745337476.jpg\" style=\"height:750px; width:1000px\" /></p>\r\n\r\n<p><em>Biến ban c&ocirc;ng th&agrave;nh những g&oacute;c &quot;chill&quot; ấn tượng</em></p>\r\n\r\n<h2>4 nguy&ecirc;n tắc thiết kế ban c&ocirc;ng đẹp v&agrave; hợp l&yacute;</h2>\r\n\r\n<p>Khi thiết kế ban c&ocirc;ng, d&ugrave; căn hộ/ng&ocirc;i nh&agrave; c&oacute; diện t&iacute;ch lớn hay nhỏ cũng cần phải tu&acirc;n theo những nguy&ecirc;n tắc nhất định. Dưới đ&acirc;y l&agrave; 4 nguy&ecirc;n tắc được ứng dụng nhiều nhất trong thiết kế:</p>\r\n\r\n<h3>Đảm bảo k&iacute;ch thước ban c&ocirc;ng ti&ecirc;u chuẩn</h3>\r\n\r\n<p>Trong Quyết định số 04/2008/QĐ-BTBXD của Bộ trưởng Bộ X&acirc;y dựng, tại mục 2.8.10. Phần nh&agrave; được ph&eacute;p nh&ocirc; qu&aacute; chỉ giới đường đỏ trong trường hợp chỉ giới x&acirc;y dựng tr&ugrave;ng với chỉ giới đường đỏ, th&igrave; k&iacute;ch thước ban c&ocirc;ng phải đ&aacute;p ứng c&aacute;c y&ecirc;u cầu nhất định.</p>\r\n\r\n<p>V&igrave; vậy, khi bạn thiết kế x&acirc;y dựng ban c&ocirc;ng cần đảm bảo c&aacute;c y&ecirc;u cầu sau:</p>\r\n\r\n<p>- Trong khoảng kh&ocirc;ng lấy từ mặt vỉa h&egrave; l&ecirc;n tới độ cao 3,5m, mọi bộ phận của nh&agrave; đều kh&ocirc;ng được nh&ocirc; ra qu&aacute; chỉ giới đường chỉ đỏ, trừ c&aacute;c trường hợp: Từ độ cao 1m trở l&ecirc;n, tất cả c&aacute;c bậu cửa, gờ chỉ, phần trang tr&iacute; được ph&eacute;p vượt đường đỏ kh&ocirc;ng qu&aacute; 0,2m.</p>\r\n\r\n<p>- Trong độ cao từ 3,5m (so với mặt vỉa h&egrave;) trở l&ecirc;n, c&aacute;c bộ phận cố định của ng&ocirc;i nh&agrave; như ban c&ocirc;ng, &ocirc;-văng, s&ecirc;-n&ocirc;, m&aacute;i đua&hellip; được vượt qu&aacute; chỉ giới đường đỏ theo c&aacute;c điều kiện sau:</p>\r\n\r\n<p>Độ vươn ra được đo từ chỉ giới đường đỏ tới m&eacute;p ngo&agrave;i c&ugrave;ng của phần nh&ocirc; ra, phải được nhỏ hơn chiều rộng vỉa h&egrave; &iacute;t nhất 1,0m, phải đảm bảo c&aacute;c quy định về an to&agrave;n mạng lưới điện v&agrave; tu&acirc;n thủ quy định về quản l&yacute; x&acirc;y dựng &aacute;p dụng cụ thể cho khu vực;</p>\r\n\r\n<p>Vị tr&iacute; độ cao v&agrave; độ vươn ban c&ocirc;ng phải thống nhất hoặc tạo được nhịp điệu trong h&igrave;nh thức c&ocirc;ng tr&igrave;nh kiến tr&uacute;c.<br />\r\nTr&ecirc;n phần nh&ocirc; ra chỉ được l&agrave;m ban c&ocirc;ng, kh&ocirc;ng được che chắn tạo th&agrave;nh l&ocirc;-gia hay buồng ở.<br />\r\nĐối với nh&agrave; mặt phố c&oacute; vỉa h&egrave;, ban c&ocirc;ng cần c&oacute; độ cao so với vỉa h&egrave; l&agrave; lớn hơn hoặc bằng 3,5m, c&aacute;ch m&eacute;p vỉa h&egrave; tối thiểu 1m.</p>\r\n\r\n<h3>X&aacute;c định phong c&aacute;ch trước khi thiết kế ban c&ocirc;ng</h3>\r\n\r\n<p>Trong&nbsp;<strong>nguy&ecirc;n tắc thiết kế ban c&ocirc;ng</strong>, việc x&aacute;c định phong c&aacute;ch thiết kế rất quan trọng. Điều n&agrave;y gi&uacute;p bạn khai th&aacute;c hợp l&yacute; diện t&iacute;ch kh&ocirc;ng gian m&agrave; kh&ocirc;ng l&agrave;m mất t&iacute;nh thẩm mỹ của ng&ocirc;i nh&agrave;.&nbsp;</p>\r\n\r\n<p>V&iacute; dụ: Bạn chọn phong c&aacute;ch thiết kế ban c&ocirc;ng &Aacute; Đ&ocirc;ng, c&aacute;ch thiết kế sẽ theo xu hướng mềm mại kết hợp với c&acirc;y xanh tự nhi&ecirc;n.</p>\r\n\r\n<p>Nếu bạn chọn phong c&aacute;ch thiết kế ban c&ocirc;ng ch&acirc;u &Acirc;u, c&aacute;ch thiết kế sẽ hướng đến những đường thẳng v&agrave; nội thất hiện đại.</p>\r\n\r\n<p><img alt=\"thiet-ke-ban-cong\" src=\"https://img.homedy.com/store/images/2020/12/16/thiet-ke-ban-cong-637436886470121922.jpg\" style=\"height:756px; width:1000px\" /></p>\r\n\r\n<p><em>Thiết kế ban c&ocirc;ng hơi hướng &Aacute; Đ&ocirc;ng</em></p>\r\n\r\n<h3>Thiết kế ban c&ocirc;ng ph&ugrave; hợp phong thủy</h3>\r\n\r\n<p>T&ugrave;y theo tuổi, mệnh của gia chủ m&agrave;&nbsp;<strong><em>nguy&ecirc;n tắc thiết kế ban c&ocirc;ng</em></strong>&nbsp;sao cho hợp phong thủy sẽ kh&aacute;c nhau. Phần lớn, c&aacute;c ng&ocirc;i nh&agrave; sẽ lựa chọn ban c&ocirc;ng hướng Đ&ocirc;ng để mang t&agrave;i l&ocirc;c cho gia đ&igrave;nh. Bởi đ&acirc;y l&agrave; hướng mặt trời mọc mang lại nguồn năng lượng t&iacute;ch cực v&agrave; cũng mang đến &aacute;nh s&aacute;ng tốt l&agrave;nh buổi s&aacute;ng. Từ đ&oacute;, gi&uacute;p c&aacute;c th&agrave;nh vi&ecirc;n gia đ&igrave;nh c&oacute; th&ecirc;m nguồn năng lượng t&iacute;ch cực.&nbsp;</p>\r\n\r\n<p>Ngo&agrave;i ra, hướng Nam cũng l&agrave; hướng được lựa chọn nhiều để x&acirc;y ban c&ocirc;ng bởi mang đến kh&ocirc;ng kh&iacute; trong l&agrave;nh, m&aacute;t dịu v&agrave; phong thủy tốt.</p>\r\n\r\n<p>2 hướng Bắc v&agrave; T&acirc;y n&ecirc;n tr&aacute;nh. V&igrave; nếu x&acirc;y ban c&ocirc;ng hướng Bắc ng&ocirc;i nh&agrave; sẽ hứng những cơn gi&oacute; lạnh, mưa b&atilde;o của thời tiết thất thường, ảnh hưởng đến sức khỏe. Ban c&ocirc;ng hướng T&acirc;y bị &aacute;nh nắng mặt trời chiếu cả ng&agrave;y, kh&oacute; tản nhiệt, l&agrave;m kh&ocirc;ng kh&iacute; trong nh&agrave; kh&ocirc;ng tốt.</p>\r\n\r\n<h3>Thiết kế ban c&ocirc;ng đảm bảo t&iacute;nh&nbsp;tiện dụng</h3>\r\n\r\n<p>Bạn ho&agrave;n to&agrave;n c&oacute; thể thiết kế ban c&ocirc;ng đảm bảo t&iacute;nh tiện dụng bằng c&aacute;ch ch&uacute; &yacute; đến nhu cầu giải tr&iacute; của gia đ&igrave;nh. V&iacute; dụ, nếu bạn y&ecirc;u th&iacute;ch ngồi ngắm thi&ecirc;n nhi&ecirc;n, c&oacute; thể thiết kế ban c&ocirc;ng k&egrave;m bộ b&agrave;n ghế nhỏ. Nếu bạn th&iacute;ch c&oacute; nguồn rau sạch, c&oacute; thể thiết kế ban c&ocirc;ng th&agrave;nh nơi trồng rau đ&ecirc; gần gũi với thi&ecirc;n nhi&ecirc;n.&nbsp;</p>\r\n\r\n<p><img alt=\"thiet-ke-ban-cong-1\" src=\"https://img.homedy.com/store/images/2020/12/16/thiet-ke-ban-cong-1-637436890955255649.png\" style=\"height:668px; width:1000px\" /></p>\r\n\r\n<p><em>Thiết kế ban c&ocirc;ng mang t&iacute;nh tiện dụng</em></p>\r\n\r\n<h2>Những lưu &yacute; khi trồng c&acirc;y xanh ở ban c&ocirc;ng nh&agrave; đẹp</h2>\r\n\r\n<p>C&acirc;y xanh gi&uacute;p ban c&ocirc;ng trở n&ecirc;n đẹp v&agrave; thu h&uacute;t hơn. Tuy nhi&ecirc;n kh&ocirc;ng phải ai cũng biết c&aacute;ch trồng c&acirc;y xanh hợp l&yacute;. Dưới đ&acirc;y l&agrave; một số hướng dẫn:&nbsp;</p>\r\n\r\n<p>Lựa chọn c&acirc;y trồng c&oacute; khả năng chịu nhiệt, chịu nắng: v&igrave; ban c&ocirc;ng l&agrave; nơi đ&oacute;n nhiều nắng gi&oacute; n&ecirc;n khi trồng c&acirc;y bạn n&ecirc;n lựa chọn được những loại c&acirc;y c&oacute; sức chịu nắng tốt như xương rồng, hoa 1o giờ, hoa đ&aacute;, hoa sứ,&hellip;</p>\r\n\r\n<p>Lựa chọn k&iacute;ch thước c&acirc;y trồng: v&igrave; ban c&ocirc;ng l&agrave; nơi c&oacute; k&iacute;ch thước rất nhỏ n&ecirc;n bạn chỉ n&ecirc;n lựa chọn những loại c&acirc;y trồng c&oacute; k&iacute;ch thước ph&ugrave; hợp để trang tr&iacute; gi&uacute;p l&agrave;m nổi bật kh&ocirc;ng gian xanh của bạn.</p>\r\n\r\n<p>Trồng c&acirc;y d&acirc;y leo ban c&ocirc;ng: nếu bạn c&oacute; &yacute; tưởng trang tr&iacute; ban c&ocirc;ng bằng c&acirc;y d&acirc;y leo th&igrave; n&ecirc;n lựa chọn những c&acirc;y chịu hạn tốt, c&oacute; k&iacute;ch thước nhỏ để tr&aacute;nh tạo sức nặng lớn l&ecirc;n gi&agrave;n. Một số loại c&acirc;y ph&ugrave; hợp như t&oacute;c ti&ecirc;n, huỳnh anh, huỳnh đệ,&hellip;</p>\r\n\r\n<p>H&atilde;y ứng dụng những&nbsp;<strong>nguy&ecirc;n tắc thiết kế ban c&ocirc;ng</strong>&nbsp;tr&ecirc;n để l&ecirc;n &yacute; tưởng thiết kế một ban c&ocirc;ng ấn tượng, đẹp v&agrave; hợp phong thủy nhất. Ch&uacute;c bạn c&oacute; được một&nbsp; ban c&ocirc;ng ưng &yacute;!</p>', 'hinh45.png', '2021-01-07', 'admin', NULL, '2021-01-08 03:12:56');
INSERT INTO `tin_tuc` (`id_tin`, `tieude`, `noidung_tt`, `noidung`, `hinh_anh_tin`, `ngaydang`, `tacgia`, `created_at`, `updated_at`) VALUES
(10, '8 ý tưởng trang trí noel cho nhà ở độc lạ mà lại siêu tiết kiệm', 'Các ý tưởng trang trí noel cho nhà ở dưới đây không chỉ tiết kiệm mà còn thân thiện với môi trường. Vì vậy, cha mẹ có thể khuyến khích trẻ nhỏ cùng tham gia trang trí với mình, tăng sự sáng tạo cho trẻ. Dưới đây là một số gợi ý, bạn có thể tham khảo để biến đổi tùy không gian gia đình.', '<p><strong>M&ugrave;a lễ hội cuối năm 2020</strong>&nbsp;đến gần, chỉ cần khoảng 30 ph&uacute;t chuẩn bị, bạn ho&agrave;n to&agrave;n c&oacute; thể sở hữu những kh&ocirc;ng gian nh&agrave; ở độc đ&aacute;o&nbsp;đ&oacute;n Noel với mức chi ph&iacute; tiết kiệm, cả ng&ocirc;i nh&agrave;/căn hộ của bạn sẽ được phủ một m&agrave;u sắc noel rất ri&ecirc;ng.</p>\r\n\r\n<h2><strong>1. &Yacute; tưởng tạo c&acirc;y th&ocirc;ng Noel cho ph&ograve;ng kh&aacute;ch</strong></h2>\r\n\r\n<p>Nhắc đến noel kh&ocirc;ng thể thiếu c&acirc;y th&ocirc;ng - c&acirc;y đặc trưng nhất gi&uacute;p mang sắc m&agrave;u noel đến. Th&ocirc;ng thường,&nbsp;<a href=\"https://homedy.com/news/8-y-tuong-trang-tri-noel-cho-nha-o-doc-la-ma-lai-sieu-tiet-kiem-ne5905\"><strong>trang tr&iacute; noel</strong></a>&nbsp;cho nh&agrave; ở<strong>&nbsp;</strong>c&aacute;c gia đ&igrave;nh<strong>&nbsp;</strong>đặt c&acirc;y th&ocirc;ng ở ph&ograve;ng kh&aacute;ch. C&oacute; rất nhiều &yacute; tưởng tự tạo c&acirc;y th&ocirc;ng noel từ s&aacute;ch b&aacute;o cũ, s&aacute;ch vỏ, vỏ chai... rất th&acirc;n thiện với m&ocirc;i trường v&agrave; tiết kiệm chi ph&iacute;. Bạn c&oacute; thể tham khảo một số &yacute; tưởng c&acirc;y th&ocirc;ng noel sau:</p>\r\n\r\n<p><img alt=\"cay-thong-noel\" src=\"https://img.homedy.com/store/images/2020/12/11/cay-thong-noel-637432748757593266.jpg\" style=\"height:913px; width:607px\" /></p>\r\n\r\n<p><em>C&acirc;y th&ocirc;ng noel từ b&aacute;o cũ</em></p>\r\n\r\n<p><img alt=\"cay-thong-noel-1\" src=\"https://img.homedy.com/store/images/2020/12/11/cay-thong-noel-1-637432755046588433.jpg\" style=\"height:457px; width:607px\" /></p>\r\n\r\n<p><em>C&acirc;y th&ocirc;ng noel được tạo từ c&aacute;ch sắp xếp những cuốn s&aacute;ch trong tủ s&aacute;ch gia đ&igrave;nh</em></p>\r\n\r\n<p><img alt=\"cay-thong\" src=\"https://img.homedy.com/store/images/2020/12/11/cay-thong-1-637432762042827057.jpg\" style=\"height:607px; width:607px\" /></p>\r\n\r\n<p><em>Tự l&agrave;m c&acirc;y th&ocirc;ng noel từ cầu thang v&agrave; c&aacute;c vật trang tr&iacute; dễ thương</em></p>\r\n\r\n<p><img alt=\"cay-thong\" src=\"https://img.homedy.com/store/images/2020/12/11/cay-thong-637432762593204365.png\" style=\"height:608px; width:607px\" /></p>\r\n\r\n<p><em>C&acirc;y th&ocirc;ng noel l&agrave;m từ c&aacute;c thanh gỗ</em></p>\r\n\r\n<p><strong>&gt;&gt;&gt; XEM TH&Ecirc;M:</strong></p>\r\n\r\n<p><strong><a href=\"https://homedy.com/news/20-y-tuong-lam-vuon-treo-doc-dao-cho-nha-pho-va-chung-cu-ne5071\">20+ &yacute; tưởng l&agrave;m vườn treo độc đ&aacute;o cho nh&agrave; phố v&agrave; chung cư</a></strong></p>\r\n\r\n<p><strong><a href=\"https://homedy.com/news/20-y-tuong-thiet-ke-san-thuong-2020-vua-sieu-dep-lai-it-chi-phi-ne5542\">20+ &yacute; tưởng thiết kế s&acirc;n thượng 2020 vừa si&ecirc;u đẹp lại &iacute;t chi ph&iacute;</a></strong></p>\r\n\r\n<p>Nh&igrave;n chung, c&oacute; rất nhiều&nbsp;<strong>c&aacute;ch l&agrave;m c&acirc;y th&ocirc;ng noel tại nh&agrave;&nbsp;</strong>đơn giản, dễ thực hiện. V&igrave; vậy, bạn h&atilde;y thỏa sức s&aacute;ng tạo để mang đến kh&ocirc;ng kh&iacute; gi&aacute;ng sinh trong gia đ&igrave;nh vừa đẹp lại si&ecirc;u rẻ.</p>\r\n\r\n<h2><strong>2. Trang tr&iacute; bằng&nbsp;tường nh&agrave; đ&oacute;n Noel</strong></h2>\r\n\r\n<p>Bạn h&atilde;y sử dụng những mẫu decal d&aacute;n tường mang kh&ocirc;ng kh&iacute; noel để gi&uacute;p ng&ocirc;i nh&agrave; của m&igrave;nh tr&ocirc;ng sắc m&agrave;u noel hơn.</p>\r\n\r\n<p><img alt=\"trang-tri-tuong-giang-sinh\" src=\"https://img.homedy.com/store/images/2020/12/11/trang-tri-tuong-giang-sinh-637432764563042364.jpg\" style=\"height:607px; width:607px\" /></p>\r\n\r\n<p><em>Trang tr&iacute; tường nh&agrave; bằng decal đ&oacute;n noel</em></p>\r\n\r\n<p><img alt=\"noel\" src=\"https://img.homedy.com/store/images/2020/12/11/noel-637432765931666363.jpg\" style=\"height:607px; width:607px\" /></p>\r\n\r\n<p><em>Trang tr&iacute; tường nh&agrave; bằng c&aacute;c d&acirc;y treo đ&oacute;n noel</em></p>\r\n\r\n<h2><strong>3. Mang sắc m&agrave;u noel đến cầu thang</strong></h2>\r\n\r\n<p>C&oacute; kh&aacute; nhiều c&aacute;ch t&ocirc; điểm cho cầu thang của gia đ&igrave;nh bạn tr&agrave;n ngập sắc m&agrave;u noel. Một v&agrave;i gợi &yacute; l&agrave; bạn c&oacute; thể th&ecirc;m d&acirc;y th&ocirc;ng cuốn, v&ograve;ng nguyệt kế, những chiếc nơ, những b&oacute; hoa kh&ocirc;... Sau đ&oacute;, bạn c&oacute; thể lắp th&ecirc;m những d&acirc;y đ&egrave;n nh&aacute;y lung linh nh&eacute;.&nbsp;</p>\r\n\r\n<p><img alt=\"trangtricauthang\" src=\"https://img.homedy.com/store/images/2020/12/11/trangtricauthang-637432769043813760.jpg\" style=\"height:607px; width:607px\" /></p>\r\n\r\n<p><em>Kh&ocirc;ng kh&iacute; gi&aacute;ng sinh ngập tr&agrave;n cầu thang</em></p>\r\n\r\n<p><img alt=\"trangtricauthang\" src=\"https://img.homedy.com/store/images/2020/12/11/trangtricauthang1-637432769553615711.jpg\" style=\"height:607px; width:607px\" /></p>\r\n\r\n<p><em>Một gợi &yacute; trang tr&iacute; cầu thang noel kh&aacute;c</em></p>\r\n\r\n<h2><strong>4. Th&ecirc;m sắc m&agrave;u noel cho căn bếp</strong></h2>\r\n\r\n<p>Bếp l&agrave; nơi c&aacute;c th&agrave;nh vi&ecirc;n trong gia đ&igrave;nh qu&acirc;y quần, v&igrave; thế bạn h&atilde;y ch&uacute; trọng trong kh&ocirc;ng gian n&agrave;y. Chỉ cần v&agrave;i điểm nhấn như decal d&aacute;n, &ocirc;ng gi&agrave; noel hay v&ograve;ng nguyệt quế cũng sẽ khiến kh&ocirc;ng gian bếp gia đ&igrave;nh bạn trở n&ecirc;n thu h&uacute;t hơn rất nhiều.</p>\r\n\r\n<p><img alt=\"trangtribep\" src=\"https://img.homedy.com/store/images/2020/12/11/trangtribep1-637432772859586625.jpg\" style=\"height:455px; width:607px\" /></p>\r\n\r\n<p><em>Trang tr&iacute; kh&ocirc;ng gian bếp ngập sắc noel</em></p>\r\n\r\n<h2><strong>5. Phỏng ngủ ch&agrave;o đ&oacute;n gi&aacute;ng sinh</strong></h2>\r\n\r\n<p>Để mang kh&ocirc;ng gian gi&aacute;ng sinh v&agrave;o phỏng ngủ, bạn chỉ cần thay đổi ga gối giường bằng những gam m&agrave;u đỏ ấm &aacute;p của noel. Đừng qu&ecirc;n th&ecirc;m những d&acirc;y đ&egrave;n nh&aacute;y để kh&ocirc;ng gian th&ecirc;m lung linh nh&eacute;.&nbsp;</p>\r\n\r\n<p><img alt=\"trang-tri-phong-ngu\" src=\"https://img.homedy.com/store/images/2020/12/11/trangtriphongngu-637432775663213054.jpg\" style=\"height:607px; width:607px\" /></p>\r\n\r\n<p><em>Ph&ograve;ng ngủ đậm chất gi&aacute;ng sinh</em></p>\r\n\r\n<p><img alt=\"trang-tri-phong-ngu\" src=\"https://img.homedy.com/store/images/2020/12/11/trangtriphongngu1-637432776064050067.jpg\" style=\"height:607px; width:607px\" /></p>\r\n\r\n<p><em>Ph&ograve;ng ngủ mang hơi hướng noel</em></p>\r\n\r\n<h2><strong>6. Trang tr&iacute; noel cho cửa ra v&agrave;o</strong></h2>\r\n\r\n<p>Cửa ra v&agrave;o ch&iacute;nh l&agrave; nơi để bạn thỏa sức trang tr&iacute;, t&ugrave;y theo khả năng v&agrave; nhu cầu m&agrave; bạn c&oacute; thể lựa chọn cầu kỳ hoặc đơn giản. Nơi đ&acirc;y gi&uacute;p mang đến cảm gi&aacute;c kh&ocirc;ng kh&iacute; lễ hội chung, bạn c&oacute; thể lựa chọn c&acirc;y th&ocirc;ng noel, &ocirc;ng gi&agrave; noel, đ&egrave;n nh&aacute;y để nhấn mạnh.</p>\r\n\r\n<p><img alt=\"trang-tri-cua\" src=\"https://img.homedy.com/store/images/2020/12/11/trang-tri-cua-637432777275609700.jpg\" style=\"height:607px; width:607px\" /></p>\r\n\r\n<p><em>Trang tr&iacute; cửa ra v&agrave;o đ&oacute;n noel</em></p>\r\n\r\n<p><img alt=\"cuanha\" src=\"https://img.homedy.com/store/images/2020/12/11/cuanha-637432778431815610.jpg\" style=\"height:682px; width:607px\" /></p>\r\n\r\n<p><em>Cửa nh&agrave; ngập sắp noel</em></p>\r\n\r\n<h2><strong>7. Trang tr&iacute; cửa sổ đ&oacute;n noel</strong></h2>\r\n\r\n<p>Theo c&aacute;c c&acirc;u chuyện, &ocirc;ng gi&agrave; Noel sẽ đến thăm v&agrave; tặng qu&agrave; cho trẻ nhỏ th&ocirc;ng qua cửa sổ. V&igrave; vậy, nếu gia đ&igrave;nh bạn c&oacute; trẻ nhỏ h&atilde;y ch&uacute; &yacute; trong trang tr&iacute; khu vực n&agrave;y. Nếu bạn l&agrave; người th&iacute;ch đơn giản th&igrave; chỉ cần decal gi&aacute;n, đ&egrave;n nh&aacute;y, v&ograve;ng nguyệt quế... m&agrave; th&ocirc;i.</p>\r\n\r\n<p><img alt=\"cuaso\" src=\"https://img.homedy.com/store/images/2020/12/11/cuaso-637432781047876843.jpg\" style=\"height:433px; width:607px\" /></p>\r\n\r\n<p><em>Trang tr&iacute; cửa sổ ngập sắc m&agrave;u noel</em></p>\r\n\r\n<p><img alt=\"cua-so\" src=\"https://img.homedy.com/store/images/2020/12/11/cuaso1-637432781359071954.jpg\" style=\"height:607px; width:607px\" /></p>\r\n\r\n<p><em>Decal d&aacute;n gi&uacute;p kh&ocirc;ng kh&iacute; noel ngập tr&agrave;n</em></p>\r\n\r\n<h2><strong>8. Trang tr&iacute; trần nh&agrave;</strong></h2>\r\n\r\n<p>Bạn c&oacute; thể vận dụng<strong>&nbsp;&yacute;&nbsp;tưởng trang tr&iacute; noel cho nh&agrave; ở&nbsp;</strong>để l&agrave;m cho trần nh&agrave; của gia đ&igrave;nh trở n&ecirc;n độc đ&aacute;o hơn. Trần nh&agrave; bạn c&oacute; thể treo v&ograve;ng nguyệt quế, quả chu&ocirc;ng v&agrave; c&aacute;c vật dễ thương kh&aacute;c.&nbsp;</p>\r\n\r\n<p><img alt=\"trang-tri-tran-nha\" src=\"https://img.homedy.com/store/images/2020/12/11/trang-tri-tran-nha-637432782447821318.jpg\" style=\"height:379px; width:607px\" /></p>\r\n\r\n<p><em>Treo c&aacute;c vật độc đ&aacute;o l&ecirc;n ph&iacute;a trần nh&agrave;</em></p>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave; những &yacute; tưởng&nbsp;<strong><em>trang tr&iacute; noel cho nh&agrave; ở&nbsp;</em></strong>&nbsp;gi&uacute;p bạn c&oacute; được kh&ocirc;ng gian đậm sắc m&agrave;u. C&oacute; h&agrave;ng ngh&igrave;n &yacute; tưởng trang tr&iacute; độc đ&aacute;o, cập nhật li&ecirc;n tục theo xu hướng&nbsp;cho nh&agrave; ở bạn c&oacute; thể tham khảo tại&nbsp;<strong><a href=\"https://homedy.com/feed/yeu-nha\">cộng đồng y&ecirc;u nh&agrave; đẹp Homedy</a>.&nbsp;</strong></p>', 'hinh40 - Copy.png', '2021-01-07', 'admin', NULL, '2021-01-08 03:13:16'),
